<script>    /**     * Remember:Jquery "(document).ready(function(){" wont works in modal     * That's why need to use jquery in modal by the following way     */    $('body').on('click', '.click-article', function(){        var selectArticle = $(this).attr("name");        $('input[name="content"]').val(selectArticle);        $('input[name="content_id"]').val($(this).attr("id"));    });</script><!-- Modal content--><div class="modal-content">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal">&times;</button>		<h4 class="modal-title">Select a single article</h4>	</div>	<div class="modal-body">        <style>            table span:hover{color:#0000ff;text-decoration: underline;cursor: hand}        </style>        <table class="table table-bordered table-condensed table-striped table-hover">            <tr class="info">                <th>Article name</th>                <th class="text-right">Article an Article</th>            </tr>            @if(count($articles))                @foreach($articles as $article)                    <tr>                        <td><span class="click-article" name="{{ $article->article_name }}" id="{{ $article->id }}" data-dismiss="modal"> {{ $article->article_name }} </span></td>                        <td class="text-right">                            @if (count($article->article_category))                                {{ $article->article_category->category_name }}                            @endif                        </td>                    </tr>                @endforeach            @else                <tr>                    <td colspan="3" class="text-center text-danger">{{ "No article found" }}</td>                </tr>            @endif        </table>	</div>	<div class="modal-footer">		<button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>	</div></div>