<script>    /**     * Remember:Jquery "(document).ready(function(){" wont works in modal     * That's why need to use jquery in modal by the following way     */    $('body').on('click', '.click-article', function(){        var selectedCat = $(this).attr("name");        $('input[name="content"]').val(selectedCat);        $('input[name="content_id"]').val($(this).attr("id"));    });</script><!-- Modal content--><div class="modal-content">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal">&times;</button>		<h4 class="modal-title">Select an Article Category</h4>	</div>	<div class="modal-body">        <style>            table span:hover{color:#0000ff;text-decoration: underline;cursor: hand}        </style>        <table class="table table-bordered table-condensed table-striped table-hover">            <tr class="info">                <th class="text-center">Category Name</th>            </tr>            @if(count($categories))                @foreach($categories as $category)                    <tr>                        <td class="text-center"><span class="click-article" name="{{ $category->category_name }}" id="{{ $category->id }}" data-dismiss="modal"> {{ $category->category_name }} </span></td>                    </tr>                @endforeach            @else                <tr>                    <td colspan="3" class="text-center text-danger">{{ "No article found" }}</td>                </tr>            @endif        </table>	</div>	<div class="modal-footer">		<button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>	</div></div>